textarea.css("width","100%");
if (data && data.fields && data.rows && data.rows.length > 0) {
  var fieldIndex = getDataObjectFieldIndex(data, "value");
  if (fieldIndex > 0) {
    var value = data.rows[0][fieldIndex];
    if (value) {
      var lines = value.split("\n");
      var len = lines[0].length;    
      for (var i in lines) {
        if (lines[i].length > len) len = lines[i].length;
      }
      // max width is screen width less left pos and padding / 10
      var maxWidth = ($(window).width() - textarea.offset().left - 80) / 10;
      // clamp len to maxWidth
      if (len > maxWidth) len = maxWidth;
      // lower clamp at 25 chars
      if (len > 25) textarea.css("width",len * 10);
    }
  }
}

