validation.javaScript:
var name = $("#rapid_P1_C7").val();
if (!name) return "An application name must be provided";
if (!name.match(new RegExp("^[0-9A-Za-z\._-]+$"))) return "An application name can only contain characters a-z, A-Z, 0-9, ., -, and _";

var name = $("#rapid_P1_C7").val();
var version = $("#rapid_P1_C48_").val();
if (!version) return "An application version must be provided";
if (!version.match(new RegExp("^[0-9A-Za-z\._-]+$"))) return "An application version can only contain characters a-z, A-Z, 0-9, ., -, and _";
var exists = false;
$.ajax({
    url: "designer?action=checkVersion&name=" + name + "&version=" + version,
    type: "GET",    
    async: false,                
    success: function(result) {
        if (result) {
            exists = true;
        }
    }
});
if (exists) return "This version of the application already exists";
