  var data = getDataStoreData("rapid_P0_C469_",rapid_P0_C469_details);
  if (data && data.fields && data.fields.length > 0 && data.rows && data.rows.length > 0) {
    for (var i in data.fields) {
      if (data.fields[i] == "users") break;
    }
    var users = data.rows[0][i];
    for (var j in users) {
      var user = users[j];
      if (user && user.name && user.name.toLowerCase() == value.toLowerCase()) return "User already exists in application";
    }
  }
} else {
  return "User name must be specified";
}

