validation.javaScript:
if (value) {
  var pass = true;
  var users = $("#rapid_P0_C823_").find("td:first-child").each( function() {
    var user = $(this).text();
    if (user && user.toLowerCase() == value.toLowerCase()) {
      pass = false;
      return;
    }
  });
  if (!pass) return "User already exists";
} else {
  return "User name must be provided";
}
