resource.contents:
	                
ul.list, .list ul {
	list-style-type: none;
	padding: 0px;
	margin: 0px;
}

.list ul {
	background: inherit;
}

.list {
	display: inline-block;
	background: #FFF;
}

li.listOption {
	cursor: pointer;
}           

span.listSelectedCount {
	float: left;
} 

span.listSlider {
	float: right;
	font-family: FontAwesome-solid;
	pointer-events: none;
}

control.getHtmlFunction:
	        
// assume we have no initial values	
var values = [];
// if an initial value was given split on comma
if (this.value) values = this.value.split(",");
// assume there is no header text
var headerText = "";
// assume there are no options
var options = "";
// assume no entries have been selected
var selectedCount = 0;
// check we have options
if (this.options) {
	// if the options are a string turn them in an array
	if ($.type(this.options) == "string") this.options = JSON.parse(this.options);
	// loop the options
	for (var i in this.options) {
		// get the value
		var value = this.codes ? this.options[i].value : this.options[i].text;
		// assume not selected
		var selected = false;
		// loop the initial values
		for (var j in values) {
			if (values[j].trim() == value.trim()) {
				selected = true;
				if (this.options[i].text) headerText += this.options[i].text + ", ";
				selectedCount ++;
			}
		}
		// append the options
		options += "<li class='listOption" + (selected ? " listSelected" : "") + "'" + (this.slide ? " style='display:none'" : "") + " data-value='" + escapeApos(value) + "'>" + this.options[i].text + "</li>";
	}
}
// check we have a header or can slide
if (this.header || this.slide) {
	// put something in headerText if nothing
	if (!headerText) headerText = this.header;
	if (!headerText) headerText = "Header text";
	if (this.slide && selectedCount > 0) {
		headerText = headerText.substr(0, headerText.length - 2);
		options = "<span class='listSelectedCount'>" + selectedCount + "</span><span class='fas listSlider'>&#xf078;</span><li class='listHeader" + (this.slide ? "" : " listVisible") + "'>" + headerText + "</li>" + options;
	} else {
		options = "<span class='listSelectedCount' style='display:none;'></span><span class='fas listSlider'>&#xf078;</span><li class='listHeader" + (this.slide ? "" : " listVisible") + "'>" + headerText + "</li>" + options;
	}
}
// place into the list
var html = this.fixedHeight ? 
	"<div id='" + this.id + "' class='list" + getStyleClasses(this) + "' style='display:inline-grid;height:2.2em;'><ul style='display:block;'>" + options + "</ul></div>" : 
	"<ul id='" + this.id + "' class='list" + getStyleClasses(this) + "'>" + options + "</ul>";
// return
return html;

control.setDataJavaScript:
	        
if (data != null && data !== undefined) {
	var control = $("#" + id);
	var list = control.is("ul") ? control : control.find("ul");
	data = makeDataObject(data, field);
	if (data.rows && data.fields) {
		var hide = !list.is(".listVisible");
		for (var i in data.rows) {
			var row = data.rows[i];		
			var text = "";
			var value = "";
			if (data.fields) {
				for (var j in data.fields) {
					if (data.fields[j].toLowerCase() == "text") text = data.rows[i][j];
					if (data.fields[j].toLowerCase() == "value") value = data.rows[i][j];
				}
			}
			if (!text) text = row[0];
			if (!value && row[1]) value = row[1];
			list.append("<li class='listOption'" + (hide ? " style='display:none;'" : "") + " data-value='" + value + "'>" + text + "</li>");
			list.children().last().click( details, function(ev) {
				if (!ev.data.multi) list.children().removeClass("listSelected");
				$(ev.target).toggleClass("listSelected");
				if (ev.data.slide) {
					var text = "";
					var header = list.children("li.listHeader");
					var count = 0;
					var selectedCount = list.children("span.listSelectedCount");
					list.children("li.listSelected").each( function() { 
							text += $(this).html() + ", "; 
							count ++;
					});				
					if (count > 0) {
						text = text.substr(0, text.length - 2);
						selectedCount.text(count).show();
					} else {
						text = ev.data.header;
						selectedCount.hide();
					}					
					if (!details.multi) selectedCount.hide();									
					header.html(text);		
				}
				if (details.slide && details.slide.selectAway) {
					header.removeClass("listVisible");
					list.removeClass("listVisible");
					list.children("li.listOption").slideUp(250, function() {
						list.children("span.listSlider").html("&#xf078;");
					});
				}
				list.trigger("change");
			});
		}
	} 
}

control.initJavaScript:
	        
var control = $("#" + id);
var list = control.is("ul") ? control : control.find("ul");
if (details.slide) {
	var header = list.children("li.listHeader");
	var selectedCount = list.children("span.listSelectedCount");
	header.click( function(ev) {
		header.toggleClass("listVisible");
		list.toggleClass("listVisible");
		list.children("li.listOption").slideToggle(250, function() {
			if (header.hasClass("listVisible")) {
				list.children("span.listSlider").html("&#xf077;");
			} else {
				list.children("span.listSlider").html("&#xf078;");
			}
		});  				
	});	
	
	// if clickAway, listen for clicks that don't originate from the list and close it
	if (details.slide.clickAway) {
		$("body").click(function(ev) {
			if (!$.contains(control[0], ev.target)) {
				header.removeClass("listVisible");
				list.removeClass("listVisible");
				list.children("li.listOption").slideUp(250, function() {
					list.children("span.listSlider").html("&#xf078;");
				});
			}
		});
	}
}

list.children("li.listOption").click( details, function(ev) {
	if (!ev.data.multi) list.children().removeClass("listSelected");
	$(ev.target).toggleClass("listSelected");
	if (ev.data.slide) {
		var text = "";
		var count = 0;
		list.children("li.listSelected").each( function() { 
				text += $(this).html() + ", "; 
				count ++;
		});
		if (count > 0) {
			text = text.substr(0, text.length - 2);
			selectedCount.text(count).show();
		} else {
			text = ev.data.header;
			selectedCount.hide();
		}
		header.html(text);	
	}
	if (details.slide && details.slide.selectAway) {
		header.removeClass("listVisible");
		list.removeClass("listVisible");
		list.children("li.listOption").slideUp(250, function() {
			list.children("span.listSlider").html("&#xf078;");
		});
	}
	list.trigger("change");	
});

runtimeProperty.getPropertyFunction:
	   	            
var control = $("#" + id);
var list = control.is("ul") ? control : control.find("ul");
var value = "";
list.children("li.listSelected").each( function() { 
	value += $(this).attr("data-value") + ","; 
});	        
if (value) {
	return value.substring(0, value.length - 1);
} else {
	return null;
}

runtimeProperty.setPropertyJavaScript:
			        
if (data != null && data !== undefined) {
	var data = makeDataObject(data);			        
	var values = [];
	if (data && data.rows[0][0]) {
		values = data.rows[0][0].split(",");
	} else {
		values = null;
	}
	var control = $("#" + id);
	var list = control.is("ul") ? control : control.find("ul");
	var header = list.children("li.listHeader");
	var selectedCount = list.children("span.listSelectedCount");
	if (!details.multi || values == null) list.children().removeClass("listSelected");
	if (values) {	
		var headerText = "";
		var selectCount = 0;
		list.children("li.listOption").each( function() {		
			var option = $(this);
			var match = false;
			for (var i in values) {			
				if (values[i].trim() == option.attr("data-value")) {
					option.addClass("listSelected");
					headerText += option.text() + ", ";
					selectCount ++;
					match = true;
					break;
				} 
				option.removeClass("listSelected");
			}
		});
		if (selectCount > 0) {
			headerText = headerText.substring(0,headerText.length - 2);			
			if (details.multi && details.slide) selectedCount.text(selectCount).show();
		} else {			
			headerText = details.header;
			selectedCount.hide();
		}
		header.html(headerText);	
	} else {
		header.html(details.header);
		selectedCount.hide();
	}
}
