resource.contents:


var _signatureControls = {};
var _signatureCanvases = [];

// JQuery is ready! 
$(document).ready( function() {

	$(window).resize(function(ex) {

		var ratio =  window.devicePixelRatio || 1;

		_signatureCanvases.forEach(function(canvas) {

			try {
				var context = canvas.getContext("2d");
				var data = context.getImageData(0, 0, $(canvas).width(), $(canvas).height());
				var url = canvas.toDataURL();
				var img = new Image();
				img.src = url;
				canvas.width = $(canvas).width() * ratio;
				canvas.height = $(canvas).height() * ratio;
				context.scale(ratio, ratio);
				//if (data && data instanceof ImageData) context.putImageData(data, 0, 0, 0, 0, $(canvas).width(), $(canvas).height());
				img.onload = function() {
					context.drawImage(img, 0, 0, $(canvas).width(), $(canvas).height());
				};
			} catch(ex) {
				console.log("Error resizing signature " + i + " : " + ex);
			}

		});

	});

});

control.initDesignJavaScript:

var canvas = this.object[0];
var signature = new _pageIframeWindow[0].SignaturePad(canvas,{
  backgroundColor : $(canvas).css("background-color")
});
signature.fromDataURL("data:image/png;base64,");

control.getDataFunction:

var signature = _signatureControls[id];
if (signature && signature.points && signature.points.length > 0) {
	return signature.toDataURL();
} else {
	return null;
}

control.setDataJavaScript:


var isPageLoadEvent = function(f) {
	return f.caller && /^Event_pageload/.test(f.caller.name) || isPageLoadEvent(f.caller);
};

var delayedCall = isPageLoadEvent(setData_signature) ?
	function(f) { setTimeout(f, 300) } : 
	function(f) { f() };

delayedCall(function() {
var signature = _signatureControls[id];
if (signature) {
	data = makeDataObject(data, field);
	if (data && data.rows) {
		// call signature from data url method
		signature.fromDataURL(data.rows[0][0]);
		// put a point in points array so getData will not send null
		signature.points = [0];
	} else {
		signature.clear();
	}
}
});

control.initJavaScript:

var canvas = $("#" + id)[0];
if (canvas) {
	var minWidth = 2;
	var maxWidth = 2;
	switch (details.lineThickness) {
	  case "thin" :
	    minWidth = 0.5;
	    maxWidth = 1.5;
	  break;
	  case "thick" :
	    minWidth = 5;
	    maxWidth = 7.5;
	  break;
	}
	var signature = new SignaturePad(canvas,{
	  backgroundColor : "rgb(255,255,255)",
	  penColor: details.lineColour,
	  minWidth: minWidth,
	  maxWidth: maxWidth
	});
	_signatureControls[id] = signature;
	var ratio =  window.devicePixelRatio || 1;
	if ($(canvas).width() > 0 && $(canvas).height() > 0) {
		// more accurate because of border but 0 if hidden
		canvas.getContext("2d").scale(ratio, ratio);
	}
	_signatureCanvases.push(canvas);
}
